import { useState, useRef } from "react";
import { useTokenContext } from "../../context/TokenContext";
import { useNavigate } from "react-router-dom";
import { Navigate, useParams } from "react-router-dom";
import PostList from "../../components/PostList";
import usePostsByIdUser from "../../hooks/usePostsByIdUser";
import Post from "../../components/Post";

const UserPostPage = () => {
  const { token, loggedUser } = useTokenContext();
  const { idUser } = useParams();

  const { post, setPost, errorMessage, loading } = usePostsByIdUser(idUser);
  const { id: idPost, text, image, place, rate, owner } = post;
  console.log("post.[0]", post);
  const {
    username,
    avatar,
    role,
    createdAt,
    email,
    id: idUserLogged,
  } = loggedUser;

  const navigate = useNavigate();

  // Si no hay token, redireccionamos a "/login""
  if (!token) {
    return <Navigate to="/login" />;
  }

  for (let posts of post) {
    posts++;
    console.log("post", post);
    return (
      <Post
        id={posts.idPost}
        text={posts.text}
        image={posts.image}
        avatar={posts.avatar}
        username={posts.username}
        email={posts.email}
        place={posts.place}
        idUser={posts.idUser}
        rate={posts.rate}
        owner={posts.owner}
        createdAt={posts.createdAt}
      />
    );
  }
  return (
    <section>
      <h2>perfil de {username}</h2>
    </section>
  );
};

export default UserPostPage;

