import { useState } from "react";
import { useTokenContext } from "../../context/TokenContext";

const CommentForm = (idPost, setShowCommentModal) => {
  const [text, setText] = useState("");
  const { token, loggedUser } = useTokenContext();

  return (
    <form
      onClick={async (e) => {
        try {
          e.preventDefault();

          const res = await fetch(
            `http://localhost:4000/posts/${idPost}/coments`,
            {
              method: "POST",
              headers: {
                Authorization: token,
                "Content-Type": "application/json",
              },
              body: JSON.stringify({ text: text }),
            }
          );

          const body = await res.json();
          console.log("body.data6", body);

          if (!res.ok) {
            throw new Error(body.message);
          }

          // addCommentToPost({
          //   id: idPost,
          //   newAvg: body.data.rate,
          //   valueRated: currentStar,
          // });
        } catch (error) {
          console.error(error);
        }
      }}
    >
      <label htmlFor="text">
        Deja un comentario
        {/* (m√°s adelante ternario con post.comets<0 "se el primero") */}:
      </label>
      <input
        id="text"
        value={text}
        onChange={(event) => {
          setText(event.target.value);
        }}
      />

      <button>Buscar</button>
    </form>
  );
};

export default CommentForm;
