import { useState } from "react";
import { useTokenContext } from "../../context/TokenContext";
import { Link } from "react-router-dom";

//para renderizarlo tras crearlo  PROBAR ESTOOO
//necesito ahora setPosts y Posts, primero filter para buscar el post, luego le añado la info para que lo pinte y set para que renderice

const CommentForm = ({ idPost, idUser, coments }) => {
  const [text, setText] = useState("");
  const { token, loggedUser } = useTokenContext();

  return (
    <section>
      <ul className="ComentList">
        {coments.map((coment) => {
          const {
            avatar: avatarComent,
            id: idComent,
            username: usernameComent,
            text: textComent,
            idUser: idUserComent,
          } = coment;

          return (
            <li key={idComent}>
              <Link to={`/posts/${id}`}></Link>
            </li>
          );
        })}
      </ul>

      <form
        onClick={async (e) => {
          try {
            e.preventDefault();
            console.log("idPostCommCompo", idUser);

            const res = await fetch(
              `http://localhost:4000/posts/${idPost}/coments`,
              {
                method: "POST",
                headers: {
                  Authorization: token,
                  "Content-Type": "application/json",
                },
                body: JSON.stringify({ text, idUser, idPost }),
              }
            );

            const body = await res.json();

            if (!res.ok) {
              throw new Error(body.message);
            }

            /*    addCommentToPost({
            id: idPost,
            newAvg: body.data.rate,
            valueRated: currentStar,
          }); */
          } catch (error) {
            console.error(error);
          } finally {
            setText("");
          }
        }}
      >
        <label htmlFor="text">
          Deja un comentario
          {/* (más adelante ternario con post.comets<0 "se el primero") */}:
        </label>
        <input
          id="text"
          type="text"
          value={text}
          required
          onChange={(event) => {
            setText(event.target.value);
          }}
        />

        <button>Enviar</button>
      </form>
    </section>
  );
};

export default CommentForm;
