import { NavLink, Link } from "react-router-dom";
import { useTokenContext } from "../../context/TokenContext";
import defaultAvatar from "../../assets/images/defaultAvatar.jpg";

const Nav = () => {
  const { token, setToken, loggedUser } = useTokenContext();
  const { username, avatar } = loggedUser;

  return (
    <nav>
      <ul>
        {/* Si no hay token, pintamos los enlaces a registro y login */}
        {!token && (
          <>
            <li>
              <NavLink to="/register">Registro</NavLink>
            </li>
            <li>
              <NavLink to="/login">Login</NavLink>
            </li>
          </>
        )}

        {token && (
          <>
            <li>
              <Link to="/">Posts</Link>
            </li>

            <li>
              <Link to="/new/post">Nuevo post </Link>
            </li>
            <li>
              <Link to="/profile">
                <img
                  src={
                    avatar ? `http://localhost:4000/${avatar} ` : defaultAvatar
                  }
                  alt={`${username} avatar`}
                />
              </Link>
            </li>
            <li>
              <button
                onClick={() => {
                  setToken("");
                }}
              >
                Cerrar Sesi√≥n
              </button>
            </li>
          </>
        )}
      </ul>
    </nav>
  );
};

export default Nav;
