import "./style.css";
import getTimeAgo from "../../utils/getTimeAgo";
import PostVotesStars from "../../components/PostVotesStars/index";
// import PostPhotos from "../PostPhotos";
import Modal from "../Modal";
import { useTokenContext } from "../../context/TokenContext";
import { useState } from "react";
import PostPhoto from "../PostPhoto";
import { NavLink } from "react-router-dom";

const Post = ({
  id,
  text,
  image,
  avatar,
  username,
  email,
  place,
  idUser,
  rate,
  owner,
  createdAt,
  ratedByMe,
  addVoteToPost,
}) => {
  const { token, loggedUser } = useTokenContext();
  const [showModal, setShowModal] = useState(false);

  // console.log("image", image);

  return (
    <article className="post">
      <header>
        <h3>
          <NavLink to={`/posts/${idUser}`}>{username}</NavLink>
        </h3>
        <h4>{place}</h4>
      </header>

      <p>{text}</p>
      {image?.length > 0 && <PostPhoto image={image} username={username} />}

      {/* {image?.length > 0 && <PostPhotos photos={image} text={text} />} */}

      <footer>
        <section className="PostVotes">
          <p>{parseFloat(rate).toFixed(2)}</p> {<PostVotesStars votes={rate} />}
        </section>
        <span>路</span>
        <p className="PostDateAuthor">
          Publicado por <span> {username} </span>
          {getTimeAgo(new Date(createdAt))}
        </p>

        {token && loggedUser.id !== idUser && (
          <>
            <span>路</span>
            <button
              onClick={(event) => {
                event.preventDefault();

                setShowModal(true);
              }}
            >
              Votar
            </button>
          </>
        )}
      </footer>

      {showModal && (
        <Modal setShowModal={setShowModal}>
          {!ratedByMe ? (
            <p>Escoge la puntuaci贸n que le quieres dar a la entrada:</p>
          ) : (
            <p>Has dado esta puntuaci贸n:</p>
          )}

          <PostVotesStars
            rate={ratedByMe}
            idPost={id}
            addVoteToPost={addVoteToPost}
          />
        </Modal>
      )}
    </article>
  );
};

export default Post;
