import { useState } from "react";

const CommentForm = () => {
  const [text, setText] = useState("");

  return (
    <form
      onSubmit={(event) => {
        event.preventDefault();
      }}
    >
      <label htmlFor="text">
        Deja un comentario
        {/* (m√°s adelante ternario con post.comets<0 "se el primero") */}:
      </label>
      <input
        id="text"
        value={text}
        onChange={(event) => {
          setText(event.target.value);
        }}
      />

      <button>Buscar</button>
    </form>
  );
};

export default CommentForm;
